$(function () {

    $('.menu__inner-burger').on('click', function () {
        $('.menu__inner-list').toggleClass("active-menu");
    })

    $('.header__inner-lnr').on('click', function () {
        $('.header__inner-search').toggleClass("header__active-search");
    })

    var swiper = new Swiper('.swiper-container__top', {
        slidesPerView: 1,
        loop: true,
        navigation: {
            nextEl: '.swiper-button-next__top',
            prevEl: '.swiper-button-prev__top',
        },
    });

    var swiper = new Swiper('.swiper-container__insta', {
        slidesPerView: 4,
        loop: true,
        navigation: {
            nextEl: '.swiper-button-next__insta',
            prevEl: '.swiper-button-prev__insta',
        },
    });

    var swiper = new Swiper('.swiper-container__aside', {
        slidesPerView: 1,
        loop: true,
        centeredSlides: true,
        navigation: {
            nextEl: '.swiper-button-next__slider',
            prevEl: '.swiper-button-prev__slider',
        },
    });
});